leverages = option(default=5|20|100,type=text)
threshold = option(default=0,type=range,min=0,max=220,log=true)
fadeOut = option(default=0,type=range,min=0,max=1,log=true,step=0.001)
stepScale = option(default=0.5,type=range,min=0,max=2,step=0.01)
stepValue = option(default="",type=text,placeholder="empty = ATR")
strength = option(default=0.25,type=range,min=0,max=10,step=0.01,log=true)
quote = option(default=null,type=list,options=[null, "USD", "USDT", "TUSD", "USDC"],rebuild=true)
exchange = option(default=null,type=exchange,rebuild=true)
type = option(default=perp,type=list,options=[null, "spot", "perp"],rebuild=true)
src = option(default="high/low",type=list,options=["open", "high", "low", "close","hl2","ohl3","ohlc4","hlcc4","high/low","low/high"])
color0 = option(default=rgba(0,0,0,0),type=color)
color1 = option(default=rgb(63,16,87),type=color)
color2 = option(default=rgb(76,152,134),type=color)
color3 = option(default=rgb(240,218,24),type=color)
volLength = option(default=20,type=range,min=1,max=200, step=1)
atrLength = option(default=20,type=range,min=1,max=200, step=1)
useHK = option(default=true,type=checkbox,description="Use heikinashi as source")
useLog = option(default=true,type=checkbox)
strokeWidth = option(default=0,type=number,description="For debug purpose")

if (!liquidityAtPrices) {
  // init

  lastIndex = null
  freeSlots = []
  usedSlots = []
  boundaries = {}
  liquidityAtPrices = {}
  resistances = []
  supports = []
  pendingRedraws = []
  var shortLeverage = options.leverages.toString().split(/[|,]/).map(a => +a)
  var longLeverage = shortLeverage.map(a => -a)
  resolvedLeverages = shortLeverage.concat(longLeverage)
  steps = null

  for (var i = 0; i < series.length; i++) {
    if (series[i].seriesType() !== 'BrokenArea') {
      continue
    }

    // register each available series as a slot 
    freeSlots.push({
    index: i,
    redrawAt: 0
    })

    // bind boundaries reference
    // boundaries: { [cellId: string]: *bar index* }
    series[i].setExtensionsBoundaries(boundaries)
  }
}


// process only on new candle
if (bar.length === lastIndex) {
  return
}

var customSrc = source(quote=quote,type=type,exchange=exchange)
var price = useHK ? avg_heikinashi(customSrc) : avg_ohlc_with_gaps(customSrc)

priceRef = price

// grid size
if (stepValue) {
  // absolute
  steps = stepValue
} else {
  // calculated with atr
  var hl = price.high - price.low
  var hc = Math.abs(price.high - priceRef[1].close)
  var lc = Math.abs(price.low - priceRef[1].close)
  var atr = sma(Math.max(hl, hc, lc), atrLength)

  if (bar.length < atrLength) {
    return
  }

  if (!steps) {
    steps = atr * stepScale
  }
}

buyVolume = source(vbuy, quote=quote,type=type,exchange=exchange)
sellVolume = source(vsell, quote=quote,type=type,exchange=exchange)
vol = buyVolume + sellVolume

// since we only run this indi once per bar (for performance)
// only analyze volume data of the previous bar (vol[1], buyVolume[1], sellVolume[1])
const avgBuyVolume = sma(buyVolume[1] || 0, volLength) || 0.0001;
const avgSellVolume = sma(sellVolume[1] || 0, volLength) || 0.0001;

var buyRatio = (buyVolume[1] || 0) / avgBuyVolume;
var sellRatio = (sellVolume[1] || 0) / avgSellVolume;
if (useLog) {
  buyRatio = Math.log(buyRatio + 1)
  sellRatio = Math.log(sellRatio + 1)
}

// custom src
var resolvedSrc  = priceRef[1][options.src]
if (resolvedSrc === undefined) {
  if (options.src === 'hl2') {
    resolvedSrc = (priceRef[1].high + priceRef[1].low) / 2
  } else if (options.src === 'ohl3') {
    resolvedSrc = (priceRef[1].open + priceRef[1].high + priceRef[1].low) / 3
  } else if (options.src === 'ohlc4') {
    resolvedSrc = (priceRef[1].open + priceRef[1].high + priceRef[1].low + priceRef[1].close) / 4
  } else if (options.src === 'hlcc4') {
    resolvedSrc = (priceRef[1].high + priceRef[1].low + priceRef[1].close + priceRef[1].close) / 4
  }
}

for (let i = 0; i < resolvedLeverages.length; i++) {
  var lev = (resolvedLeverages)[i]

  if (options.src === 'high/low') {
    resolvedSrc = lev > 0 ? priceRef[1].high : priceRef[1].low
  } else if (options.src === 'low/high') {
    resolvedSrc = lev > 0 ? priceRef[1].low : priceRef[1].high
  }

  // liquidation price (from resolvedSrc + % to leverages[i]) 
  var lp = (resolvedSrc + resolvedSrc * (100 / lev / 100))

  // rounded lp median (the cell id)
  var slp = Math.ceil(lp / steps) * steps

  if (!liquidityAtPrices[slp]) {
    // register cell
    var top = slp + steps / 2
    var bottom = slp - steps / 2
    liquidityAtPrices[slp] = {
      strength: 0,
      count: 0,
      top: top,
      bottom: bottom
    }

    if (lev > 0) {
        resistances.push(slp)
        resistances.sort((a, b) => a - b)
    } else {
        supports.push(slp)
        supports.sort((a, b) => b - a)
    }
  }

  // update cell
  liquidityAtPrices[slp].count++
  liquidityAtPrices[slp].strength += lev > 0 ? sellRatio : buyRatio

  var ratio = Math.min(1, liquidityAtPrices[slp].strength * liquidityAtPrices[slp].count * (options.strength / 100))

  if (ratio < 0.001) {
    continue
  }

  liquidityAtPrices[slp].color = interpolate(ratio, color0, color1, color2, color3)

  if (liquidityAtPrices[slp].strength > options.threshold) {
    if (pendingRedraws.indexOf(slp) === -1) {
      pendingRedraws.push(slp)
    }
  }
}

if (pendingRedraws.length) {

  // round of used slot(s)
  var slotsToRelease = []
  for (var i = 0; i < pendingRedraws.length; i++) {
    var cell = liquidityAtPrices[pendingRedraws[i]]
    var slot = freeSlots.find(slot => slot.redrawAt < bar.length)

    if (slot) {
      var usedIndex = freeSlots.splice(freeSlots.indexOf(slot), 1)[0].index
      usedSlots.push(usedIndex)

      if (cell.id) {
        boundaries[cell.id] = bar.length
      }

      cell.id = Math.random().toString()
      renderer.indicators[indicatorId].series[usedIndex] ={
        id: cell.id,
        time: time,
        lowerValue: cell.top,
        higherValue: cell.bottom,
        extendRight: true,
        color: cell.color
      }

      pendingRedraws.splice(i--, 1)
      slotsToRelease.push(usedIndex)
    }
  }
  for (let i = 0; i < slotsToRelease.length; i++) {
    var plotIndexToFree = usedSlots.splice(usedSlots.indexOf(slotsToRelease[i]), 1)[0]

    freeSlots.push({
      index: plotIndexToFree,
      redrawAt: bar.length + 1
    })
  }
}

for (let i = 0; i < supports.length; i++) {
  if (
   price.low <= supports[i] || liquidityAtPrices[supports[i]].count <= 0
  ) {
    boundaries[liquidityAtPrices[supports[i]].id] = bar.length
    delete liquidityAtPrices[supports[i]]
    supports.splice(i--, 1)
  } else {
    if (fadeOut) {
      liquidityAtPrices[supports[i]].count -= fadeOut
    } else {
      break;
    }
  }
}

for (let i = 0; i < resistances.length; i++) {
  if (
   price.high >= resistances[i] || liquidityAtPrices[resistances[i]].count <= 0
  ) {
    boundaries[liquidityAtPrices[resistances[i]].id] = bar.length
    delete liquidityAtPrices[resistances[i]]
    resistances.splice(i--, 1)
  } else {
    if (fadeOut) {
      liquidityAtPrices[resistances[i]].count -= fadeOut
    } else {
      break;
    }
  }
}

// set reference to bar index : avoid process next tick
lastIndex = bar.length

// define available series
if (renderer.indicators[indicatorId].series[0]) {
  brokenarea(renderer.indicators[indicatorId].series[0] || { time: time }, strokeColor=options.strokeColor, strokeWidth=options.strokeWidth)
}
if (renderer.indicators[indicatorId].series[1]) {
